SELECT 
	*
FROM 
    order_role ora
WHERE 1=1
    AND ora.orderid = #{orderid}
    AND ora.roleid = #{roleid}
    AND ora.roletype = #{roletype}
    AND ora.isfinish = #{isfinish}
    ORDER BY ora.id desc
    limit 1

