mapper.select:
SELECT
	count(om.id) ORDER_NUM,
	sum(om.num) LUG_NUM
FROM
	order_main om,
	order_role oro
WHERE 1=1
    AND om.id = oro.orderid
	AND oro.roleid = #{roleid}
	AND oro.roletype = #{roletype}
	AND oro.isfinish = #{isfinish}
	AND oro.desttype = #{desttype}
	AND oro.destaddress = #{destaddress}

SELECT oro.destaddress, oro.destaddrname, count(oro.destaddress) send_count FROM order_main om, order_role oro WHERE 1=1 AND om.id = oro.orderid AND om.status != 'CANCELLED' AND om.isvalid = 'Y' AND oro.desttype = 'TRANSITCERTER' AND oro.roletype = 'ROLE_TRANSIT_SEND' AND oro.isfinish in ('UNFINISHED','ONGOING') AND date(oro.arrivedtime) = curdate()
AND oro.roleid = #{dmanuserid}
GROUP BY oro.destaddress

SELECT oro.destaddress, oro.destaddrname, count(oro.destaddress) task_count FROM order_main om, order_role oro WHERE 1=1 AND om.id = oro.orderid AND om.status != 'CANCELLED' AND om.isvalid = 'Y' AND oro.desttype = 'TRANSITCERTER' AND oro.roletype = 'ROLE_TRANSIT_TASK' AND oro.isfinish in ('UNFINISHED','ONGOING') AND date(oro.arrivedtime) = curdate()
AND oro.roleid = #{dmanuserid}
GROUP BY oro.destaddress

SELECT oro.destaddress, oro.destaddrname, count(oro.destaddress) task_count FROM order_main om, order_role oro WHERE 1=1 AND om.id = oro.orderid AND om.status != 'CANCELLED' AND om.isvalid = 'Y' AND oro.desttype = 'SERVICECERTER' AND oro.roletype = 'ROLE_AIRPORT_TASK' AND oro.isfinish in ('UNFINISHED','ONGOING') AND date(oro.arrivedtime) = curdate()
AND oro.roleid = #{dmanuserid}
GROUP BY oro.destaddress

SELECT oro.destaddress, oro.destaddrname, count(oro.destaddress) send_count FROM order_main om, order_role oro WHERE 1=1 AND om.id = oro.orderid AND om.status != 'CANCELLED' AND om.isvalid = 'Y' AND oro.desttype = 'SERVICECERTER' AND oro.roletype = 'ROLE_AIRPORT_SEND' AND oro.isfinish in ('UNFINISHED','ONGOING') AND date(oro.arrivedtime) = curdate()
AND oro.roleid = #{dmanuserid}
GROUP BY oro.destaddress

SELECT DISTINCT oro.orderid, om.*, case when (SELECT oa.srcaddrtype !='AIRPORTCOUNTER' FROM order_address oa WHERE oa.orderid= om.id) then '' when ((SELECT oa.srcaddrtype ='AIRPORTCOUNTER' FROM order_address oa WHERE oa.orderid= om.id) and (SELECT count(1) = 1 FROM order_role ora WHERE 1=1 AND ora.orderid = om.id AND ora.roletype = 'ROLE_AIRPORT_TASKED' ORDER BY ora.id desc LIMIT 1)) then 'Y' else 'N' end airport_tasked_flag FROM order_main om, order_role oro WHERE 1=1 AND om.id = oro.orderid AND om.status != 'CANCELLED' AND om.isvalid = 'Y' AND om.orderno like '%${orderno}%' AND oro.roleid = #{dmanuserid}
and not EXISTS ( select 1 from order_role oee where oee.orderid = om.id and oro.roleid = #{dmanuserid} and oee.isfinish in ('UNFINISHED', 'ONGOING') ) AND (date( om.taketime) = curdate() OR DATE_FORMAT(om.taketime,'%Y-%m-%d %H:%i:%S') = timestamp(adddate(date(sysdate()),1)))
AND oro.isfinish in('UNFINISHED', 'ONGOING')
AND (select TIMESTAMPDIFF(Minute, CURTIME(), oro.arrivedtime) as daysum)<#{countdown}

